import React, { useEffect, useState } from 'react';
import axios from 'axios';
import cheerio from 'cheerio';
import { Row, Card } from 'react-bootstrap';
import './articles.css'

const PitchforkArticles = ({ numArticles = 4 }) => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchPitchforkArticles = async () => {
      const url = "https://pitchfork.com/news/";

      try {
        const response = await axios.get(url);
        const $ = cheerio.load(response.data);

        const fetchedArticles = $('.module.latest-module.collection-module.news-module.latest-module--horizontal')
          .slice(0, numArticles)
          .map((index, element) => {
            const title = $(element).find('h2').text();
            const image = $(element).find('img').attr('src');
            const articleUrl = $(element).find('a').attr('href');

            return { title, image, articleUrl };
          })
          .get();

        setArticles(fetchedArticles);
      } catch (error) {
        console.error('Error fetching articles:', error);
      }
    };

    fetchPitchforkArticles();
  }, [numArticles]);

  return (
   
      <Row className="row-cols-1 row-cols-md-4 g-4">
        {articles.map((article, index) => (
          <div key={index} className="col">
            <Card className = "article-card">
              <Card.Img variant="top" src={article.image} alt={article.title} />
              <Card.Body classname = 'card-body'>
                <Card.Title className = 'card-title article-title'>{article.title}</Card.Title>
                <Card.Subtitle className="card-subtitle">
                  {'Read more'}
                </Card.Subtitle>
              </Card.Body>
            </Card>
          </div>
        ))}
      </Row>
  
  );
};

export default PitchforkArticles;
