import React, { useState } from 'react';
import axios from 'axios';
import './review.css';

function Review() {
  const [reviewBody, setReviewBody] = useState('');
  const [score, setScore] = useState('');

  async function submitScore(e) {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/score', {
        score,
      });
    } catch (error) {
      console.error(error);
    }
  }

  async function submitReview(e) {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/review', {
        reviewBody,
      });
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <div className="review-container">
      <form action="POST">
        <div className="review-inputs">
          <div className="review-item score">
            <textarea
              id="review-score"
              className="review-score"
              maxLength={3}
              onChange={(e) => {
                setScore(e.target.value);
              }}
              placeholder="0-100"
              rows={1}
              cols={5}
              wrap="soft"
            />
            <button className = 'rate-button' onClick={submitScore}>RATE</button>
          </div>
          <div className="review-item revbody">
            <textarea
              id="review-body"
              className="review-body"
              maxLength={5000}
              onChange={(e) => {
                setReviewBody(e.target.value);
              }}
              placeholder="Write your review (optional)"
              cols={100}
              wrap="soft"
            />
            <button onClick={submitReview}>POST</button>
          </div>
        </div>
      </form>
    </div>
  );
}

export default Review;
