import React from 'react';
import { scaleSequential } from 'd3-scale';
import { interpolateRgb } from 'd3-interpolate';

function ReviewResults({ reviews }) {
  // Create a color scale from green to red
  const colorScale = scaleSequential(interpolateRgb('#aa1616', '#43811f')).domain([0, 100]);

  return (
    <div className="reviews-container">
      {reviews.map((review, index) => (
        <div key={index} className="review-row">
          <div className="user-score review-text">
            <p>{review.username}</p>
            <p>Score: {review.score}</p>
            {/* Colored bar */}
            <div
              className="score-bar"
              style={{
                background: colorScale(review.score),
                width: `60px`,
              }}
            ></div>
          </div>
          <div className="review-text">
            <p>{review.reviewbody}</p>
          </div>
          {index !== reviews.length - 1 && <hr className="separator" />} {/* Line separator */}
        </div>
      ))}
    </div>
  );
}

export default ReviewResults;
