import React, { useState, useEffect } from "react";

import axios from 'axios';
import { Link } from 'react-router-dom'; 

import './review.css';


function Review() {
  const [reviewBody, setReviewBody] = useState('');
  const [score, setScore] = useState('');
  const [userData, setUserData] = useState(null);




  async function submitScore(e) {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/review', {
        score, reviewBody
      });
      // Clear the score field after successful submission
      setScore('');
      // Display a success message or perform any other action
      alert('Score submitted successfully!');
    } catch (error) {
      console.error(error);
      // Handle errors if the submission fails
    }
  }
  


  return (
    <div className="review-container mb-3">
      <form action="POST">
        <div className="review-inputs">
          <div className="review-item score">
           
            <textarea
              id="review-score"
              className="review-score"
              maxLength={3}
              onChange={(e) => {
                setScore(e.target.value);
              }}
              placeholder="0-100"
              rows={1}
              cols={5}
              wrap="soft"
            />
            <button className = 'rate-button' onClick={submitScore}>RATE</button>
          </div>
          <div className="review-item revbody">
            <textarea
              id="review-body"
              className="review-body"
              maxLength={5000}
              onChange={(e) => {
                setReviewBody(e.target.value);
              }}
              placeholder="Write your review (optional)"
              cols={100}
              wrap="soft"
            />
            <button className = 'rate-button review-button' onClick={submitScore}>SUBMIT REVIEW</button>
          </div>
        </div>
      </form>
    </div>
  );
}

export default Review;
