import React, { useState, useEffect } from "react";
import { Link, useLocation, useNavigate  } from 'react-router-dom';
import './Sidebar.css'; // Import CSS file for styling
import './home.css'
import HomeIcon from '../Assets/Icons/HomeIcon.png'
import HomeIconActive from '../Assets/Icons/HomeIconActive.png'
import SearchIcon from '../Assets/Icons/SearchIcon.png'
import SearchIconActive from '../Assets/Icons/SearchIconActive.png'
import ProfileIcon from '../Assets/Icons/ProfileIcon.png'
import ProfileIconActive from '../Assets/Icons/ProfileIconActive.png'
import axios from "axios"

import wallpaper from '../Assets/Icons/wallpaper.jpg'

const linkStyles = {
  textDecoration: 'none', // Remove underline
  color: 'inherit', // Inherit the color from the parent
};

const Sidebar = () => {
  const [userData, setUserData] = useState(null);


  const location = useLocation(); // Get the current URL location
  const navigate = useNavigate(); // Access the history object for navigation
  const [historyStack, setHistoryStack] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(-1);

  const handleNavigation = (direction) => {
    const newIndex = currentIndex + direction;

    if (newIndex >= 0 && newIndex < historyStack.length) {
      setCurrentIndex(newIndex);
      navigate(historyStack[newIndex]);
    }
  };

  const pushToHistory = (path) => {
    setHistoryStack([...historyStack.slice(0, currentIndex + 1), path]);
    setCurrentIndex(currentIndex + 1);
    navigate(path);
  };

  const canGoBack = currentIndex > 0;
  const canGoForward = currentIndex < historyStack.length - 1;
 
  
  // Function to check if the link is active
  const isActiveLink = (link) => {
    return location.pathname === link;
  };

  

  useEffect(() => {
    // Make an API call to fetch user data from the backend
    axios.get("http://localhost:3000/session")
      .then(response => {
        // Handle successful response
        setUserData(response.data); // Assuming the response contains user data
      })
      .catch(error => {
        // Handle error
        console.error("Error fetching user data:", error);
    });


    

    pushToHistory(location.pathname);
  }, [location]);

  


 

 


  return (
    <div className="sidebar" >

        <div className = {`button-item`}>
            <button className = 'history-button' onClick={() => handleNavigation(-1)} disabled={!canGoBack} > 
              <span className = 'button-text '>{'<'}</span>
            </button>
            <button className = 'history-button' onClick={() => handleNavigation(1)} disabled={!canGoForward} >
              <span className = 'button-text '>{'>'}</span>
            </button>

        </div>

        

       
        
      
        <Link to = {'/'} style={linkStyles}>
          <div className = {`sidebar-item ${isActiveLink('/') ? 'active-tab' : ''}`}>
              <img className = 'sidebar-icon' src = {isActiveLink('/home') ? HomeIconActive : HomeIcon }/>
              <h1 className = 'sidebar-text'>Home</h1>

          </div>
        </Link>
        <Link to = {'/search'} style={linkStyles}>
          <div className = {`sidebar-item ${isActiveLink('/search') ? 'active-tab' : ''}`}>
              <img className = 'sidebar-icon' src = {isActiveLink('/search') ? SearchIconActive : SearchIcon }/>
              <h1 className = 'sidebar-text'>Search</h1>

          </div>
        </Link>

        {userData ? ( // Conditionally render user account or login link
        <Link to={`/user/${userData._id}`} style={linkStyles}>
          <div className={`sidebar-item ${isActiveLink('/profile') ? 'active-tab' : ''}`}>
            <img className='sidebar-icon' src={isActiveLink('/profile') ? ProfileIconActive : ProfileIcon} />
            <h1 className='sidebar-text'>Account</h1>
          </div>
        </Link>
        ) : (
        <Link to={'/login'} style={linkStyles}>
          <div className={`sidebar-item`}>
            <img className='sidebar-icon' src={ProfileIcon} />
            <h1 className='sidebar-text'>Login</h1>
          </div>
        </Link>
      )}


      {userData ? ( // Conditionally render user account or login link
        <Link to={`/user/${userData._id}`} style={linkStyles}>
          <div className={`sidebar-item ${isActiveLink('/profile') ? 'active-tab' : ''}`}>
            <img className='sidebar-icon' src={isActiveLink('/profile') ? ProfileIconActive : ProfileIcon} />
            <h1 className='sidebar-text'>Account</h1>
          </div>
        </Link>
        ) : (
        <></>
      )}




      </div>
    
      
   
  );
};

export default Sidebar;
