import React, { useEffect, useState } from 'react';
import axios from 'axios';
import cheerio from 'cheerio';

const PitchforkArticles = ({ numArticles = 4 }) => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchPitchforkArticles = async () => {
      const url = "https://pitchfork.com/news/";

      try {
        const response = await axios.get(url);
        const $ = cheerio.load(response.data);

        const fetchedArticles = $('.module.latest-module.collection-module.news-module.latest-module--horizontal')
          .slice(0, numArticles)
          .map((index, element) => {
            const title = $(element).find('h2').text();
            const image = $(element).find('img').attr('src');
            const articleUrl = $(element).find('a').attr('href');

            return { title, image, articleUrl };
          })
          .get();

        setArticles(fetchedArticles);
      } catch (error) {
        console.error('Error fetching articles:', error);
      }
    };

    fetchPitchforkArticles();
  }, [numArticles]);

  return (
    <div className="container">
      <Row className = ''>
        {articles.map((article, index) => (
          <div key={index} className="col-md-3 mb-3">
            <div className="card">
              <img src={article.image} className="card-img-top" alt={article.title} />
              <div className="card-body">
                <h5 className="card-title">{article.title}</h5>
                <a href={`https://pitchfork.com${article.articleUrl}`} className="">Read More</a>
              </div>
            </div>
          </div>
        ))}
      </Row>
    </div>
  );
};

export default PitchforkArticles;
