import React, { useEffect, useState } from 'react';
import axios from 'axios';
import cheerio from 'cheerio';
import { Row, Card } from 'react-bootstrap';

const PitchforkArticles = ({ numArticles }) => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchPitchforkArticles = async () => {
      const url = 'https://pitchfork.com/news/';

      try {
        const response = await axios.get(url);
        const $ = cheerio.load(response.data);

        const fetchedArticles = $(
          '.module.latest-module.collection-module.news-module.latest-module--horizontal'
        )
          .slice(0, numArticles)
          .map((index, element) => {
            const title = $(element).find('h2').text();
            const image = $(element).find('img').attr('src');
            const articleUrl = $(element).find('a').attr('href');

            return { title, image, articleUrl };
          })
          .get();

        setArticles(fetchedArticles);
      } catch (error) {
        console.error('Error fetching articles:', error);
      }
    };

    fetchPitchforkArticles();
  }, [numArticles]);

  return (
    <Row xs={1} md={3} className="g-4">
      {articles.map((article, index) => (
        <Card key={index} style={{ width: '18rem' }}>
          <Card.Img variant="top" src={article.image} alt={article.title} />
          <Card.Body>
            <Card.Title>{article.title}</Card.Title>
            <a href={article.articleUrl} className="btn btn-primary">
              Read More
            </a>
          </Card.Body>
        </Card>
      ))}
    </Row>
  );
};

export default PitchforkArticles;
