import puppeteer from 'puppeteer';

const fetchPitchforkArticles = async () => {
  const url = "https://pitchfork.com/news/";

  const browser = await puppeteer.launch();
  const page = await browser.newPage();
  await page.goto(url);

  const allArticles = await page.evaluate(() => {
    const articles = document.querySelectorAll('.module.latest-module.collection-module.news-module.latest-module--horizontal');

    return Array.from(articles).slice(0, 3).map((article) => {
      const title = article.querySelector('h2').innerText;
      const image = article.querySelector('img')?.src; // Extract the image src
      const url = article.querySelector('a').href;

      return { title, image, url };
    });
  });

  await browser.close();

  return allArticles;
};

export default fetchPitchforkArticles
